# ISBN 号码

```c
#include <iostream>
#include<cstring>
using namespace std;

int main() {

	char s[20];
	int num[20] = { 0 };
	int index = 0;
	cin >> s;
	int len = strlen(s);
	for (int i = 0; i < len; i++){
		if (s[i] == '-'){
			continue;
		}
		else{
			num[index++] = s[i] - '0';
		}
	}
	int sum = 0;
	for (int i = 0; i < index - 1; i++){
		sum += num[i] * (i + 1);
	}
	sum = sum % 11;

	if (sum == 10 && s[len - 1] == 'X' || sum == num[index - 1]){
		cout << "Right" << endl;
	}
	else{
		if (sum == 10)
			s[len - 1] = 'X';
		else
			s[len - 1] = sum + '0';
		cout << s << endl;
	}
	return 0;
}
```

