# 公共钥匙盒

```c
#include <iostream>
#include <cmath>
#include <algorithm>
using namespace std;

int num[1005];

struct keys
{
	int keyNum;
	int startTime;
	int endTime;
	int flag;
}keySet[1005];

int flag[1005];

bool compare(keys key1, keys key2){
	return key1.startTime < key2.startTime;
}

int main() {
	int n, k;
	cin >> n >> k;
	int maxLength = 0;
	for (int i = 0; i < k; i++){
		cin >> keySet[i].keyNum >> keySet[i].startTime >> keySet[i].endTime;
		keySet[i].flag = 0;
		flag[i] = i + 1;
		if (maxLength < keySet[i].startTime + keySet[i].endTime){
			maxLength = keySet[i].startTime + keySet[i].endTime;
		}
	}
	sort(keySet, keySet + k, compare);

	/*for (int i = 0; i < k; i++){
		cout << keySet[i].keyNum << " " << keySet[i].startTime << " " << keySet[i].endTime << endl;
		}*/


	for (int i = 1; i <= maxLength; i++){
		for (int j = 1; j <= n; j++){

			if (keySet[j].flag == 0 && keySet[j].startTime >= i && keySet[j].endTime <= i){
				keySet[j].flag = 1;
				flag[keySet[j].keyNum] = -1;
			}

			if (keySet[j].flag == 1 && i > keySet[j].endTime + keySet[j].startTime){
				for (int k = 0; k < n; k++){
					if (flag[k] == -1){
						flag[k] = keySet[j].keyNum;
						keySet[j].flag = 0;
						break;
					}
				}
			}
		}
	}

	for (int i = 0; i < n; i++){
		cout << flag[i] << endl;
	}
	return 0;
}
```

# 除法

```c
#include <iostream>
#include <algorithm>
using namespace std;
long long tree[101024];
int n, m, a[101024];
int lowbit(int x)
{
	return -x&x;
}
void update(int x, int v)
{
	for (int i = x; i <= n; i += lowbit(i))
		tree[i] += v;
}
long long getsum(int x)
{
	long long ans = 0;
	for (int i = x; i > 0; i -= lowbit(i))
		ans += tree[i];
	return ans;
}
int main()
{
	int op, l, r;
	int v;
	cin >> n >> m;
	for (int i = 1; i <= n; ++i)
	{
		cin >> a[i];
		update(i, a[i]);
	}
	for (int i = 0; i < m; i++)
	{
		cin >> op;
		if (op == 1)
		{
			cin >> l >> r >> v;
			if (v == 1)
				continue;
			while (l <= r)
			{
				if (a[l] >= v&&a[l] % v == 0)
				{
					update(l, -(a[l] - a[l] / v));
					a[l] /= v;
				}
				++l;
			}
		}
		else if (op == 2)
		{
