# include <iostream>
# include <queue>
# include <functional>
using namespace std;

typedef pair<int, int> pii;
const int maxn = 100000 + 100;
const int INF = INT_MAX;
struct node
{
	int from, to, dist;
	node(int from, int to, int dist) :from(from), to(to), dist(dist){}
};
int n, m;
vector<node> edges;
vector<int> g[maxn];
bool visited[maxn];
int d[maxn];
void AddEdge(int from, int to, int dist)
{
	edges.push_back(node(from, to, dist));
	g[from].push_back(edges.size() - 1);
}
void dij(int start)
{
	priority_queue<pii, vector<pii>, greater<pii> >q;//d和from
	for (int i = 0; i<n; i++) d[i] = INF;
	d[start] = 0;
	memset(visited, 0, sizeof(visited));
	q.push(make_pair(0, start));
	while (!q.empty()){
		pii x = q.top(); q.pop();
		int u = x.second;
		if (visited[u]) continue;
		visited[u] = true;
		for (int i = 0; i<g[u].size(); i++){
			node &e = edges[g[u][i]];
			//if (d[e.to]>max(d[u], e.dist)){//改变松弛策略
			//	d[e.to] = max(d[u], e.dist);
			//	q.push(make_pair(d[e.to], e.to));
			//}
			if (d[e.to]> d[u] + e.dist){//改变松弛策略
				d[e.to] = d[u] + e.dist;
				q.push(make_pair(d[e.to], e.to));
			}
		}
	}
}
int main()
{
	cin >> n >> m;
	for (int i = 0; i<m; i++){
		int a, b, c;
		cin >> a >> b >> c;
		AddEdge(a - 1, b - 1, c);// 只写一个代表：有向图
		//AddEdge(b - 1, a - 1, c); //两个同时写代表：无向图
	}

	dij(0);
	cout << d[n - 1] << endl;
	return 0;
}
