# 除法

```c
#include <iostream>
#include <algorithm>
using namespace std;
long long tree[101024];
int n, m, a[101024];
int lowbit(int x)
{
	return -x&x;
}
void update(int x, int v)
{
	for (int i = x; i <= n; i += lowbit(i))
		tree[i] += v;
}
long long getsum(int x)
{
	long long ans = 0;
	for (int i = x; i > 0; i -= lowbit(i))
		ans += tree[i];
	return ans;
}
int main()
{
	int op, l, r;
	int v;
	cin >> n >> m;
	for (int i = 1; i <= n; ++i)
	{
		cin >> a[i];
		update(i, a[i]);
	}
	for (int i = 0; i < m; i++)
	{
		cin >> op;
		if (op == 1)
		{
			cin >> l >> r >> v;
			if (v == 1)
				continue;
			while (l <= r)
			{
				if (a[l] >= v&&a[l] % v == 0)
				{
					update(l, -(a[l] - a[l] / v));
					a[l] /= v;
				}
				++l;
			}
		}
		else if (op == 2)
		{
			cin >> l >> r;
			cout << getsum(r) - getsum(l - 1) << endl;
		}
	}
	return 0;
}
```

