# Z 形扫描

```c
#include <iostream>
#include <string>
using namespace std;
int n;
int num[505][505];
const int RIGHT = 1; //向右走
const int DOWN = 2; //向下走
const int LEFTDOWN = 3; //向做下走
const int RIGHTUP = 4;  //向右上走

#define max 505

string to_String(int n)
{
	int m = n;
	int i = 0, j = 0;
	char s[max];
	char ss[max];
	while (m > 0)
	{
		s[i++] = m % 10 + '0';
		m /= 10;
	}
	s[i] = '\0';

	i = i - 1;
	while (i >= 0)
	{
		ss[j++] = s[i--];
	}
	ss[j] = '\0';

	return ss;
}
void run(){

	//从第一个位置开始，x为横坐标，y为纵坐标，注意x,y在二维数组中的位置
	int x = 0;
	int y = 0;
	//输出要求有空格隔开
	string result = to_String(num[y][x]) + " ";
	//方向变量的初始值
	int direction = 0;

	//下面就开始出发走咯
	while (!(x == n - 1 && y == n - 1)){//循环直至到达终点（最右下角的位置）
		//先判断下一步往哪个方向走
		if (direction == 0){   //为0说明还没走出第一步，所以接着应该往右边走一步
			direction = RIGHT;
		}
		else if (direction == RIGHT){ //上一次方向向右，下一步应该向左下或者右上
			if (x - 1 >= 0 && y + 1 < n){    //左下可走
				direction = LEFTDOWN;
			}
			else {  //只能走右上了
				direction = RIGHTUP;
			}
		}
		else if (direction == DOWN){   //上一次方向向下，下一步应该向左下或者右上
			if (x - 1 >= 0 && y + 1 < n){    //左下可走
				direction = LEFTDOWN;
			}
			else {  //只能走右上了
				direction = RIGHTUP;
			}
		}
		else if (direction == LEFTDOWN){   //上一次向左下，如果可以，下一步应该继续向左下，否则 向右或者向下走
			if (y + 1 < n && x - 1 >= 0){    //先判断能否继续向左下
				direction = LEFTDOWN;
			}
			else if (y + 1 < n){  //然后判断能否向下走
				direction = DOWN;
			}
			else {      //最后只能向右走了
				direction = RIGHT;
			}
		}
		else if (direction == RIGHTUP){    //上一次向右上，如果可以，下一步应该继续向右上，否则向右或者下走
			if (x + 1 < n && y - 1 >= 0){  //先判断能否继续向右上
				direction = RIGHTUP;
			}
			else if (x + 1 < n){  //然后判断能否向右走
				direction = RIGHT;
			}
			else{            //最后只能向下走了
				direction = DOWN;
			}
		}

		//根据上面确定的方向来走出下一步
		switch (direction){
		case RIGHT: x = x + 1; break;
		case DOWN: y = y + 1; break;
		case LEFTDOWN: x = x - 1; y = y + 1; break;
		case RIGHTUP: x = x + 1; y = y - 1; break;
		}

		//读取当前走到位置的数字.注意x和y的位置
		result += to_String(num[y][x]) + " ";
	}
	cout << result << endl;
}
int main()
{
	cin >> n;
	for (int i = 0; i < n; i++){
		for (int j = 0; j < n; j++){
			cin >> num[i][j];
		}
	}
	run();
	return 0;
}
```

# 日期计算

```c
#include <iostream>
#include <string>
using namespace std;
int n, m;

int month1[12] = { 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31 };
int month2[12] = { 31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31 };
bool isLeapYear(int year){
	if (year % 4 == 0 && year % 100 != 0 || year % 400 == 0){
		return true;
	}
	else{
		return false;
	}
}
void printMonthAndDay(int flag, int m){
	int days = m;
	int month = 1;
	int day = 0;
	int i = 0;
	if (flag == 0){
		while (days - month1[i] > 0)
		{
			days -= month1[i++];
			month++;
		}
		cout << month << "\n" << days << endl;
	}
	else{
		while (days - month2[i] > 0)
		{
			days -= month2[i++];
			month++;
		}
		cout << month << "\n" << days << endl;
	}
}
int main()
{
	cin >> n >> m;
	int flag = isLeapYear(n);
	printMonthAndDay(flag, m);
	return 0;
