# 棋局评估

```c
#include <iostream>
#include <cstring>
#include <map>
using namespace std;
int a[9];
map<int, int> mymap;

int isover(){
	for (int i = 0; i < 3; i++){    //检测列，仔细计算下，容易出错
		if (a[i] == a[i + 3] && a[i] == a[i + 6] && a[i])
			return a[i];
	}
	for (int i = 0; i < 9; i += 3){//检测行
		if (a[i] == a[i + 1] && a[i] == a[i + 2] && a[i])
			return a[i];
	}
	if (a[0] == a[4] && a[0] == a[8] && a[0])//检测对角
		return a[0];
	if (a[2] == a[4] && a[2] == a[6] && a[2])
		return a[2];
	return 0;
}

int dp(){
	int aa = 0; //状态
	for (int i = 0; i < 9; i++){
		aa = aa * 10 + a[i];
	}
	if (mymap.find(aa) != mymap.end()){ //记忆化搜索，若当前状态出现过，则不再重新计算。
		return mymap[aa];
	}

	int ct = 0;
	for (int i = 0; i < 9; i++) //找空余的0
	if (a[i])
		ct++;
	int rt = isover();
	if (rt == 1) //Alice赢
		return 10 - ct;
	if (rt == 2) // Bob赢
		return -(10 - ct);
	if (ct == 9 && rt == 0) //下满了，并且未分出胜负，则平局
		return 0;
	int an = 0;
	if (ct % 2 == 0){ //轮到Alice
		an = -100;
		for (int i = 0; i < 9; i++){
			if (a[i] == 0){    //遍历所有空格
				a[i] = 1;

				int s = dp();
				if (an < s){  //找到得分最高的
					an = s;
				}
				a[i] = 0;
			}
		}
	}
	else{   //轮到Bob
		an = 100;
		for (int i = 0; i < 9; i++){
			if (a[i] == 0){
				a[i] = 2;

				int s = dp();
				if (an > s){//找到得分最低的
					an = s;
				}
				a[i] = 0;
			}
		}
	}
	return mymap[aa] = an;
}

int main(){
	int t;
	cin >> t;
	while (t--){
		mymap.erase(mymap.begin(), mymap.end());
		for (int i = 0; i < 9; i++)   {
			cin >> a[i];
		}
		cout << dp() << endl;
	}

	return 0;
}
