   ```c++
   #include<iostream>
   using namespace std;
   int a[5], n;
   void quickSort(int left, int right){
       if (left >= right)
           return;
       int i, j, t, temp;
       i = left;
       j = right;
       temp = a[left];
       while (true){
           while (a[j] >= temp && i < j){
               j--;
           }
           while (a[i] <= temp && i < j){
               i++;
           }
           if (i < j){
               t = a[i];
               a[i] = a[j];
               a[j] = t;
           }
           else
               break;
       }
       a[left] = a[j];
       a[j] = temp;
       quickSort(left, i - 1);
       quickSort(i + 1, right);
   }
   int main()
   {
       for (int i = 1; i <= 5; i++){
           cin >> a[i];
       }
       quickSort(1, n);
       for (int i = 1; i <= 5; i++){
           cout << a[i] << " ";
       }
       cout << endl;
       return 0;
   }
   ```

